import React, { Component } from 'react';

import './menuSelection.css'

class MenuSelection extends Component {
    constructor(props) {
        super(props);
    }
    componentWillReceiveProps(props) {
        console.log(props)

        this.state = {
            pricing: [
                { type: 'Soup', price: 2.5 },
                { type: 'Wing(s)', price: .15 },
                { type: 'Burger', price: 4.95 },
                { type: 'Chicken Sandwhich', price: 5.95 },
                { type: 'Fries', price: 1.99 },
                { type: 'Pie', price: 2.95 },
                { type: 'Ice Cream', price: 2.99 },
                { type: 'Soft Drink', price: 1.5 },
                { type: 'Coffee', price: 1 }
            ]
        }
    }

    render() {
        return (
            <div>
                {this.props.appear ?
                    <div>
                        <h4 className='questionThree'>
                            What's on the Castle Shack menu?
                        </h4>

                        <div className='contentsThree'>

                        </div>
                    </div> : null}

            </div>
        )
    }
}

export default MenuSelection;
