import React, { Component } from 'react';
import { Form, FormGroup, FormControl, Button, Alert } from 'react-bootstrap'

import './guestCount.css'

class GuestCount extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
        this.informPartyAlert = this.informPartyAlert.bind(this);

        this.state = {
            partyCount: '',
            alertVisibility: STYLES.ALERT
        }
    }

    componentDidMount() {
        console.log(this.state.alertVisibility)
    }

    handleSubmit() {
        // alert(`Count: ${this.state.partyCount}`);
        const { partyCount } = this.state
        const ROUNDED_PARTY_COUNT = Math.floor(partyCount)

        if (ROUNDED_PARTY_COUNT <= 10 && ROUNDED_PARTY_COUNT > 0 && typeof ROUNDED_PARTY_COUNT === 'number') {
            console.log('----------------------------------------------');
            console.log(`Party Count Perfect: ${ROUNDED_PARTY_COUNT}`);
            console.log('----------------------------------------------');
        } else {
            console.log('----------------------------------------------');
            console.log(`Party Too Large: ${ROUNDED_PARTY_COUNT}`)
            console.log('----------------------------------------------');
        }

    }

    handleInputChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    }

    informPartyAlert() {
        return (
            <Alert bsStyle="warning" className='alert-party' style={STYLES.ALERT}>
                <strong>Holy guacamole!</strong> Enter a guest total between 1-10. 
            </Alert>
        )
    }

    render() {
        return (
            <div>
                <h4 className='question'>
                    How many guest(s) do you have?
                </h4>
                <div className='contents'>
                    <Form inline className='form'>
                        <FormGroup controlId="formInlineName">
                            <FormControl type="text" onChange={this.handleInputChange} value={this.state.partyCount} name='partyCount' placeholder="5" />
                        </FormGroup>{' '}
                    </Form>
                    <div className='submit-form-button'>
                        <Button onClick={this.handleSubmit} type="submit" bsStyle='primary'>Submit</Button>
                    </div>
                </div>

                {this.informPartyAlert()}
            </div>
        )
    }
}

const STYLES = {
    ALERT: {
        visibility: 'hidden'
    }
}

export default GuestCount;
