import React, { Component } from 'react';
import {
    Form,
    FormGroup,
    FormControl,
    Button,
    ControlLabel,
    Alert
} from 'react-bootstrap'

import './guestMembers.css'

class GuestMembers extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
        this.form = this.form.bind(this)

        this.state = {
            partyCount: '',
            alertVisible: false,
            partyCountEntered: false,
            alertVisibility: STYLES.ALERT
        }
    }

    componentDidMount() {
    }

    handleSubmit() {
    }

    handleInputChange(event) {
    }

    form() {
        return (
            <Form inline className='guestMembersForm'>
                <FormGroup controlId="formInlineName">
                    <ControlLabel>Kids</ControlLabel>{' '}
                    <FormControl className='kidsField' type="text" placeholder="ages between 0-12" />
                </FormGroup>{' '}
                <FormGroup controlId="ormInlineName" className='mt-2'>
                    <ControlLabel>Teens</ControlLabel>{' '}
                    <FormControl type="text" className='teensField' placeholder="ages between 13-19" />
                </FormGroup>{' '}
                <FormGroup controlId="ormInlineName">
                    <ControlLabel>Adults</ControlLabel>{' '}
                    <FormControl type="text" className='adultsField' placeholder="ages between 20-65" />
                </FormGroup>{' '}
                <FormGroup controlId="ormInlineName">
                    <ControlLabel>Seniors</ControlLabel>{' '}
                    <FormControl type="text" className='seniorsField' placeholder="ages 66+" />
                </FormGroup>{' '}
                <Button type="submit" bsStyle='primary' className='mt-2'>Submit</Button>
            </Form>
        )
    }

    render() {
        return (
            <div>

                <h4 className='questionTwo'>
                    Whose on your guest list?
                </h4>

                <div className='contentsTwo'>
                    {this.form()}
                </div>
                <Alert bsStyle="info" className='alertInfo'>
                    <strong>Enter the total of each of these guests attending.</strong> 
                    <p>Ex: if no kids enter <code>{0}</code></p>

                </Alert>;
            </div>
        )
    }
}

