import React, { Component } from 'react';
import { FormGroup, Radio, Checkbox, Table, ControlLabel, FormControl } from 'react-bootstrap';

import './menuSelection.css'

class MenuSelection extends Component {
    constructor(props) {
        super(props);
        this.menu = this.menu.bind(this);
        this.handleToggle = this.handleToggle.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.menuOptions = this.menuOptions.bind(this);
        this.menuItemsSelected = this.menuItemsSelected.bind(this)

        this.whichMember = this.whichMember.bind(this);

        this.state = {
            menu: [
                { type: 'Soup', price: 2.5, checked: false },
                { type: 'Wing(s)', price: .15, checked: false },
                { type: 'Burger', price: 4.95, checked: false },
                { type: 'Chicken Sandwhich', price: 5.95, checked: false },
                { type: 'Fries', price: 1.99, checked: false },
                { type: 'Pie', price: 2.95, checked: false },
                { type: 'Ice Cream', price: 2.99, checked: false },
                { type: 'Soft Drink', price: 1.5, checked: false },
                { type: 'Coffee', price: 1, checked: false }
            ],
            soupChecked: false,
            wingsChecked: false,
            burgerChecked: false,
            csChecked: false,
            friesChecked: false,
            pieChecked: false,
            iceCreamChecked: false,
            softDrinkChecked: false,
            coffeeChecked: false,
            members: ''
        }
    }


    componentWillReceiveProps(props) {
        if(props.attendingMembers !== undefined){
            this.setState({
                members: props.attendingMembers
            }, ()  => console.log('Members: ', this.state.members))
        }
    }

    componentDidMount() {
        // this.props.attendingMembers
        // console.log('MOUNTED')
    }

    table() {
        return (
            <Table striped bordered condensed hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Menu Item</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>Mark</td>
                    </tr>
                    <tr>
                        <td>2</td>
                        <td>Jacob</td>
                    </tr>
                    <tr>
                        <td>3</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>4</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>5</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>6</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>7</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>8</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>9</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                </tbody>
            </Table>
        )
    }

    handleToggle() {
        this.setState({ checkboxChecked: !this.state.checkboxChecked });
    }

    handleChange(event) {
        console.log('event: ', event.target)
    }

    whichMember() {
        console.log('-------------------------------------------');

        // const { kids, teens, adults, seniors } = this.state.members;

        var menu = [...this.state.menu];
        // console.log('menu: ', menu);
        // console.log('Members: ', this.state.members)
        // console.log('Kids: ', this.state.members.kids);
        // console.log('Kids: ', this.state.members.teens);
        // console.log('Kids: ', this.state.members.adults);
        // console.log('Kids: ', this.state.members.seniors);

        // this.state.menu.forEach(element => {
        //     menu.push(element);
        // })
        // if (kids > 0) {
        //     this.state.menu.forEach((menuItem, index) => {
        //         this.setState({
        //             menu: this.state.menu[index].price = 0
        //         })
        //     })
        // }

        console.log('-------------------------------------------');
    }

    menuItemsSelected(menuItem, index){
        console.log(menuItem);

        const INDEX_OF_MENU_ITEM = this.state.menu.indexOf(menuItem);

        console.log(INDEX_OF_MENU_ITEM)
    }

    menuOptions(){
        return (
            this.state.menu.map((menuItem, index) => {
                return (
                    <option key={index} onClick={() => this.menuItemsSelected(menuItem, index)} value={menuItem.type}>{menuItem.type}...{menuItem.price}</option>
                )
            })
        )
    }
    menu() {
        return (
            <div>
                <FormGroup controlId="formControlsSelectMultiple" className='menuOptions'>
                    <ControlLabel>Select from the menu below</ControlLabel>
                    <FormControl componentClass="select" multiple>
                        <option value="select">Select which menu item(s) you would like?</option>
                        {this.menuOptions()}
                    </FormControl>
                </FormGroup>
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.props.appear ?
                    <div>
                        <h4 className='questionThree'>
                            What's on the Castle Shack menu?
                        </h4>

                        <div className='contentsThree'>
                            {this.menu()}
                        </div>
                    </div> : null}

            </div>
        )
    }
}

export default MenuSelection;
