import React, { Component } from 'react';
import { Form, FormGroup,FormControl, Button } from 'react-bootstrap'

import './guestCount.css'

class GuestCount extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);

        this.state = {
            partyCount: 0
        }
    }

    handleSubmit(){
        // alert(`Count: ${this.state.partyCount}`);

        if (this.state.partyCount <= 10){
            console.log('----------------------------------------------');
            console.log(`Party Count Perfect: ${this.state.partyCount}`)
            console.log('----------------------------------------------');
        }else{
            console.log('----------------------------------------------');
            console.log(`Party Too Large: ${this.state.partyCount}`)


            console.log('----------------------------------------------');
        }

    }

    handleInputChange(event){
        const {name, value } = event.target;

        this.setState({
            [name]: value
        })

    }

    render() {
        return (
            <div>
                <h4 className='question'>
                    How many guest(s) do you have?
                    </h4>
                <div className='contents'>
                    <Form inline className='form'>
                        <FormGroup controlId="formInlineName">
                            <FormControl type="text" onChange={this.handleInputChange} value={this.state.partyCount} name='partyCount' placeholder="5" />
                        </FormGroup>{' '}
                    </Form>
                    <div className='submit-form-button'>
                        <Button onClick={this.handleSubmit}type="submit" bsStyle='primary'>Submit</Button>
                    </div>

                </div>
            </div>
        )
    }
}

export default GuestCount;
