import React, { Component } from 'react';
import { FormGroup, Radio, Checkbox, Table } from 'react-bootstrap';

import './menuSelection.css'

class MenuSelection extends Component {
    constructor(props) {
        super(props);
        this.menuItems = this.menuItems.bind(this);
        this.handleToggle = this.handleToggle.bind(this);

        this.state = {
            menu: [
                { type: 'Soup', price: 2.5, checked: false },
                { type: 'Wing(s)', price: .15, checked: false },
                { type: 'Burger', price: 4.95, checked: false },
                { type: 'Chicken Sandwhich', price: 5.95, checked: false },
                { type: 'Fries', price: 1.99, checked: false },
                { type: 'Pie', price: 2.95, checked: false },
                { type: 'Ice Cream', price: 2.99, checked: false },
                { type: 'Soft Drink', price: 1.5, checked: false },
                { type: 'Coffee', price: 1, checked: false }
            ],
            checkboxChecked: false,
            members: this.props.attendingMembers
        }
    }


    componentWillReceiveProps(props) {
        console.log(props)
    }


    table() {
        return (
            <Table striped bordered condensed hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Menu Item</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>Mark</td>
                    </tr>
                    <tr>
                        <td>2</td>
                        <td>Jacob</td>
                    </tr>
                    <tr>
                        <td>3</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>4</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>5</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>6</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>7</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>8</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                    <tr>
                        <td>9</td>
                        <td colSpan="2">Larry the Bird</td>
                    </tr>
                </tbody>
            </Table>
        )
    }

    handleToggle() {
        this.setState({ checkboxChecked: !this.state.checkboxChecked });
    }

    handleChange(event) {
        console.log('event: ', event)
    }

    menuItems() {
        return (
            <div>
                <FormGroup>
                    <Checkbox checked={this.state.menu[0].checked} onChange={this.handleChange} title={this.state.menu[0].type} inline>{this.state.menu[0].type}</Checkbox>
                    <Checkbox checked={this.state.menu[1].checked} onChange={this.handleChange} inline>{this.state.menu[1].type}</Checkbox>{' '}
                    <Checkbox checked={this.state.menu[2].checked} onChange={this.handleChange} inline>{this.state.menu[2].type}</Checkbox>
                </FormGroup>
                <FormGroup>
                    <Checkbox checked={this.state.menu[3].checked} onChange={this.handleChange} title={this.state.menu[3].type} inline>{this.state.menu[0].type}</Checkbox>
                    <Checkbox checked={this.state.menu[4].checked} onChange={this.handleChange} inline>{this.state.menu[4].type}</Checkbox>{' '}
                    <Checkbox checked={this.state.menu[5].checked} onChange={this.handleChange} inline>{this.state.menu[5].type}</Checkbox>
                </FormGroup>
                <FormGroup>
                    <Checkbox checked={this.state.menu[6].checked} onChange={this.handleChange} title={this.state.menu[6].type} inline>{this.state.menu[0].type}</Checkbox>
                    <Checkbox checked={this.state.menu[7].checked} onChange={this.handleChange} inline>{this.state.menu[7].type}</Checkbox>{' '}
                    <Checkbox checked={this.state.menu[8].checked} onChange={this.handleChange} inline>{this.state.menu[8].type}</Checkbox>
                </FormGroup>
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.props.appear ?
                    <div>
                        <h4 className='questionThree'>
                            What's on the Castle Shack menu?
                        </h4>

                        <div className='contentsThree'>
                            {this.menuItems()}
                        </div>
                    </div> : null}

            </div>
        )
    }
}

export default MenuSelection;
