import React, { Component } from 'react';
import { Alert, FormGroup, Button, Checkbox, ControlLabel, FormControl } from 'react-bootstrap';

import './menuSelection.css'

class MenuSelection extends Component {
    constructor(props) {
        super(props);
        this.menu = this.menu.bind(this);
        this.handleToggle = this.handleToggle.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.menuOptions = this.menuOptions.bind(this);
        this.menuItemsSelected = this.menuItemsSelected.bind(this)
        this.kidsOrdering = this.kidsOrdering.bind(this);
        this.teensOrdering = this.teensOrdering.bind(this);
        this.defaultSetting = this.defaultSetting.bind(this);
        this.changeMenuPrice = this.changeMenuPrice.bind(this);
        this.handleOrder = this.handleOrder.bind(this);
        // this.setOriginalPrices = this.setOriginalPrices.bind(this)

        this.state = {
            buttonText: 'ORDER',
            currentKidQuestion: 1,
            currentTeenQuestion: 1,
            currentAdultQuestion: 1,
            currentSeniorQuestion: 1,
            maximumOrders: 0,
            members: '',
            menu: [
                { type: 'Soup', price: 2.5, checked: false },
                { type: 'Wing(s)', price: .15, checked: false },
                { type: 'Burger', price: 4.95, checked: false },
                { type: 'Chicken Sandwhich', price: 5.95, checked: false },
                { type: 'Fries', price: 1.99, checked: false },
                { type: 'Pie', price: 2.95, checked: false },
                { type: 'Ice Cream', price: 2.99, checked: false },
                { type: 'Soft Drink', price: 1.5, checked: false },
                { type: 'Coffee', price: 1, checked: false }
            ],
            message: '',
            orderPrices: [],
            orders: 0,
            originalPricing: [],
            position: ['first', 'second', 'third', 'fourth', 'fifth', 'sixth', 'seventh', 'eigth', 'ninth', 'tenth'],
            totalOrdersMade: 0,
            orderingFor: 'kid'
        }

        // var orginal_pricing = []
        // this.state.menu.forEach(element => {
        //     orginal_pricing.push(element.price);
        // })
        // this.setOriginalPrices(orginal_pricing)

    }
    // setOriginalPrices(new_prices){
    //     this.setState({
    //         originalPricing: new_prices
    //     })
    // }

    componentWillReceiveProps(props) {
        if (props.attendingMembers !== undefined) {
            this.setState({
                members: props.attendingMembers,
                // orginalPricing: orginal_pricing,
                maximumOrders: props.attendingMembers.kids + props.attendingMembers.teens + props.attendingMembers.adults + props.attendingMembers.seniors
            }, () => {
                // this.kidsOrdering();
                // console.log('State: ', this.state);
                // console.log('props: ', props);
                // console.log(this.state.members)
                // console.log('Meniii: ', this.state.menu)
                this.handleOrder();
            })
        }
    }

    handleOrder() {
        // console.log('Sub-total: ', this.state.orderPrices)
        var original_prices = []
        this.state.menu.forEach(element => {
            original_prices.push(element.price);
        })

        this.setState({
            originalPricing: original_prices
        }, () => console.log('Original Pricing: ', this.state.originalPricing))

        const { kids, teens, adults, seniors } = this.state.members;
        if (kids > 0 && this.state.currentKidQuestion <= kids) {
            this.changeMenuPrice(0);
            this.kidsOrdering()
        } else {
            console.log('-------------------------------------------');
            console.log('Members: ', this.state.members);
            // console.log('Current Question: ', this.state.currentQuestion)
            console.log('-------------------------------------------');
            if (teens > 0) {
                this.setState({
                    orderingFor: 'teen'
                }, () => {
                    this.changeMenuPrice(.85);
                    this.teensOrdering()
                })

            }
        }
    }

    handleSubmit() {
        console.log('-------------------------------------------');
        console.log('Menu: ', this.state.menu);

        var pricing = 0;
        this.state.menu.forEach((item, index) => {
            if (item.checked) {
                pricing += item.price.toFixed(2)
            }
        })

        if(this.orderingFor === 'kid'){

        }

        switch(this.state.orderingFor){
            case 'kid': this.setState({currentKidQuestion: this.state.currentKidQuestion + 1}); break;
            case 'teen': this.setState({currentTeenQuestion: this.state.currentTeenQuestion + 1});  break;
            case 'adult': this.setState({currentAdultQuestion: this.state.currentAdultQuestion + 1});  break;
            case 'senior': this.setState({currentSeniorQuestion: this.state.currentSeniorQuestion + 1});  break;
        }

        this.setState({
            orderPrices: [pricing, ...this.state.orderPrices],
            orders: this.state.orders + 1,
            // change this for kid
            // currentQuestion: this.state.currentQuestion + 1,
        }, () => {
            console.log('ORDER PRICES SHOULD SHOW')
            console.log('Sub-total: ', this.state.orderPrices)
            this.defaultSetting();
            this.handleOrder();
        })


        // console.log(`kids: ${kids} teens: ${teens} adults: ${adults} seniors: ${seniors}`);



        // // var subtotal = 0;
        // // checking what user checked
        // this.state.menu.forEach((element, index) => {
        //     if (element.checked) {
        //         subtotal += element.price
        //     }
        // })

        // this.setState({
        //     orderPrices: [...this.state.orderPrices, subtotal],
        //     currentQuestion: 2
        // }, () => {
        //     this.defaultSetting();
        //     console.log('order prices: ', this.state.orderPrices);
        //     this.kidsOrdering()
        // })

        console.log('-------------------------------------------');
    }

    defaultSetting() {
        var menuItems = [];
        this.state.menu.forEach(element => {
            menuItems.push(element)
        });

        menuItems.forEach((element, index) => {
            element.price = this.state.originalPricing[index]
            if (element.checked) {
                element.checked = false
            }
        });

        this.setState({
            menu: menuItems
        });
    }

    handleToggle() {
        this.setState({ checkboxChecked: !this.state.checkboxChecked });
    }

    handleChange(event) {
        console.log('event: ', event.target)
    }

    teensOrdering() {
        console.log('-------------------------------------------');
        console.log(`Teens Ordering`);

        const position = this.state.position[this.state.currentTeenQuestion - 1];
        this.setState({
            message: `What will the ${position} ${this.state.orderingFor} order?`
        });


        console.log('-------------------------------------------');
    }

    kidsOrdering() {
        console.log('-------------------------------------------');
        console.log(`KIDS ORDERING INVOKED`);

        const position = this.state.position[this.state.currentKidQuestion - 1];
        this.setState({
            message: `What will the ${position} ${this.state.orderingFor} order?`
        });

        // var total_kids_ordered = 0;
        // var bool = this.state.currentQuestion <= kids;
        // console.log('bool: ', bool);
        // console.log(`current question: ${this.state.currentQuestion} and kids: ${kids}`)
        // if (kids > 0 && this.state.currentQuestion <= kids) {
        // change pricing
        // var original_pricing = [];
        // var all_menu_items = [];

        // this.state.menu.forEach((x) => {
        //     // original_pricing.push(x.price);
        //     all_menu_items.push(x);
        // });

        // all_menu_items.forEach(x =>  x.price = 0)

        // this.setState({
        //     menu: all_menu_items,
        // originalPricing: original_pricing
        // });
        // --------------------------------------------------

        console.log('-------------------------------------------');
    }

    changeMenuPrice(percentage) {
        // console.log('-------------------------------------------');
        var menuItems = this.state.menu;

        menuItems.forEach(item => {
            item.price = item.price.toFixed(2) * percentage
        });

        this.setState({
            menu: menuItems
        })
        // console.log('-------------------------------------------');
    }

    menuItemsSelected(menuItem, index) {
        var all_menu_items = [];
        this.state.menu.forEach(x => all_menu_items.push(x));
        all_menu_items[index].checked = !this.state.menu[index].checked;
        this.setState({
            menu: all_menu_items
        })
    }

    menuOptions() {
        return (
            this.state.menu.map((menuItem, index) => {
                return (
                    <option key={index} onClick={() => this.menuItemsSelected(menuItem, index)} value={menuItem.type}>{menuItem.type}..........${menuItem.price.toFixed(2)}</option>
                )
            })
        )
    }
    menu() {
        return (
            <div className='menuOptions'>
                <FormGroup controlId="formControlsSelectMultiple">
                    <ControlLabel>Select from the menu below</ControlLabel>
                    <ControlLabel>Hold <code>CMD</code> down to select multiple</ControlLabel>

                    <FormControl componentClass="select" multiple>
                        <option value="select">Select which menu item(s) you would like?</option>
                        {this.menuOptions()}
                    </FormControl>
                    <br />
                    <Button type="submit" bsStyle='primary' onClick={this.handleSubmit}>{this.state.buttonText}</Button>
                </FormGroup>
            </div>
        )
    }

    render() {
        return (
            <div>
                {this.props.appear ?
                    <div>

                        <h4 className='questionThree'>
                            What's on the Castle Shack menu?
                        </h4>

                        {this.state.message ? <div>
                            <Alert bsStyle="info" onDismiss={this.handleDismiss} className='alert-message'>
                                <h4>
                                    {this.state.message}
                                </h4>
                            </Alert>
                        </div> : null}


                        <div className='contentsThree'>
                            {this.menu()}
                        </div>



                    </div> : null}
            </div>
        )
    }
}

export default MenuSelection;
