import React, { Component } from 'react';
import {
    Form,
    FormGroup,
    FormControl,
    Button,
    ControlLabel,
    Alert
} from 'react-bootstrap'

import './guestMembers.css'

class GuestMembers extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
        this.form = this.form.bind(this)

        this.state = {
            disableInputs: false,
            inputsCorrect: false,
            partyCount: '',
            kidsTotal: '',
            teensTotal: '',
            adultsTotal: '',
            seniorsTotal: ''
        }
    }

    componentDidMount() {
    }

    handleSubmit(event) {
        event.preventDefault();

        const {
            kidsTotal,
            teensTotal,
            adultsTotal,
            seniorsTotal } = this.state;

        const INPUT_VALUES = [kidsTotal.trim(), teensTotal.trim(), adultsTotal.trim(), seniorsTotal.trim()];

        var number_counter = 0;

        console.log('----------------------------------');
        console.log(`VALUES: `);
        console.log(`Kids: ${INPUT_VALUES[0]} ${typeof INPUT_VALUES[0]}`);
        console.log(`Teens: ${INPUT_VALUES[1]} ${typeof INPUT_VALUES[1]}`);
        console.log(`Adults: ${INPUT_VALUES[2]} ${typeof INPUT_VALUES[2]}`);
        console.log(`Seniors: ${INPUT_VALUES[3]} ${typeof INPUT_VALUES[3]}`);



        console.log('----------------------------------');


        // INPUT_VALUES.forEach((element, index) => {
        //     if (typeof element === 'number') {
        //         number_counter++;
        //         var guest_totals_counted = + element;
        //         console.log('----------------------------------');
        //         console.log(`element is a number.  element: ${element}`);
        //         console.log(`Number Counter number_counter: ${number_counter}`);
        //         console.log('----------------------------------');


        //         if (number_counter === 4 && index <= 3) {
        //             if (guest_totals_counted === this.props.totalAccountedGuests) {
        //                 console.log('----------------------------------');
        //                 console.log(`Totals Guests Matches!`);
        //                 console.log(`guest_totals_counted: ${guest_totals_counted} ${this.props.totalAccountedGuests}`)
        //                 console.log('----------------------------------');
        //             }
        //         } else if (number_counter !== 4 && index === 3) {

        //             this.setState({
        //                 inputsCorrect: true
        //             }, () => {
        //                 setTimeout(() => {
        //                     this.setState({
        //                         inputsCorrect: false
        //                     })
        //                 }, 4000)
        //             });
        //         }
        //     } else {
        //         this.setState({
        //             inputsCorrect: true
        //         });
        //     }
        // })
    }

    handleInputChange(event) {
        const { name, value } = event.target;

        this.setState({
            [name]: value
        })
    }

    form() {
        return (
            <Form inline className='guestMembersForm'>
                <FormGroup controlId="formInlineName">
                    <ControlLabel>Kids</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} disabled={this.state.disableInputs} value={this.state.kidsTotal.trim()} name='kidsTotal' className='kidsField' type="text" placeholder="ages between 0-12" />
                </FormGroup>{' '}
                <FormGroup controlId="formInlineName" className='mt-2'>
                    <ControlLabel>Teens</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} disabled={this.state.disableInputs} value={this.state.teensTotal.trim()} name='teensTotal' type="text" className='teensField' placeholder="ages between 13-19" />
                </FormGroup>{' '}
                <FormGroup controlId="formInlineName">
                    <ControlLabel>Adults</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} disabled={this.state.disableInputs} value={this.state.adultsTotal.trim()} name='adultsTotal' type="text" className='adultsField' placeholder="ages between 20-65" />
                </FormGroup>{' '}
                <FormGroup controlId="formInlineName">
                    <ControlLabel>Seniors</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} disabled={this.state.disableInputs} value={this.state.seniorsTotal.trim()} name='seniorsTotal' type="text" className='seniorsField' placeholder="ages 66+" />
                </FormGroup>{' '}
                <Button type="submit" bsStyle='primary' onClick={this.handleSubmit} className='mt-2'>Submit</Button>
            </Form>
        )
    }

    alertUser() {
        return (
            <Alert bsStyle="warning" className='alert-party'>
                <strong>Holy smokes!</strong> Values most be numbers and add up to <code>{this.props.totalAccountedGuests}</code>.
            </Alert>
        )
    }

    render() {
        return (
            <div>

                {this.props.appear ?
                    <div>
                        <h4 className='questionTwo'>
                            Whose on your guest list?
                        </h4>
                        <div className='contentsTwo'>
                            {this.form()}
                        </div>
                        <Alert bsStyle="info" className='alertInfo'>
                            <strong>Enter the total of each of these guests attending.</strong>
                            <p>If there aren't any kids enter <code>{0}</code></p>
                        </Alert>
                    </div> : null}

                {this.state.inputsCorrect ?
                    this.alertUser() :
                    null
                }
            </div>
        )
    }
}

const STYLES = {
}

export default GuestMembers;
