import React, { Component } from 'react';
import {
    Form,
    FormGroup,
    FormControl,
    Button,
    ControlLabel,
    Alert
} from 'react-bootstrap'

import './guestMembers.css'

class GuestMembers extends Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
        this.form = this.form.bind(this)

        this.state = {
            partyCount: '',
            kidsTotal: 0,
            teensTotal: 0,
            adultsTotal: 0,
            seniorsTotal: 0
        }
    }

    componentDidMount() {
    }

    handleSubmit() {
    }

    handleInputChange(event) {
    }

    form() {
        return (
            <Form inline className='guestMembersForm'>
                <FormGroup controlId="formInlineName">
                    <ControlLabel>Kids</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} value={this.state.kidsTotal} className='kidsField' type="text" placeholder="ages between 0-12" />
                </FormGroup>{' '}
                <FormGroup controlId="ormInlineName" className='mt-2'>
                    <ControlLabel>Teens</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} value={this.state.teensTotal} type="text" className='teensField' placeholder="ages between 13-19" />
                </FormGroup>{' '}
                <FormGroup controlId="ormInlineName">
                    <ControlLabel>Adults</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} value={this.state.adultsTotal} type="text" className='adultsField' placeholder="ages between 20-65" />
                </FormGroup>{' '}
                <FormGroup controlId="ormInlineName">
                    <ControlLabel>Seniors</ControlLabel>{' '}
                    <FormControl onChange={this.handleInputChange} value={this.state.seniorsTotal} type="text" className='seniorsField' placeholder="ages 66+" />
                </FormGroup>{' '}
                <Button type="submit" bsStyle='primary' className='mt-2'>Submit</Button>
            </Form>
        )
    }

    render() {
        return (
            <div>

                {this.props.appear ?
                    <div>
                        <h4 className='questionTwo'>
                            Whose on your guest list?
                        </h4>
                        <div className='contentsTwo'>
                            {this.form()}
                        </div>
                        <Alert bsStyle="info" className='alertInfo'>
                            <strong>Enter the total of each of these guests attending.</strong>
                            <p>If there aren't any kids enter <code>{0}</code></p>
                        </Alert>
                    </div> : null}
            </div>
        )
    }
}

const STYLES = {
}

export default GuestMembers;
